(2, 3, '图片水平垂直居中jquery-center-image', 'jquery.centerimage.js/**\r\n * description: position a image to center of container\r\n * author: Nicolas.Z\r\n * date: April 24, 2012\r\n * requires: jQuery v1.4+\r\n */\r\n(function( $ ) {\r\n       \r\nvar dimi...', '<p>jquery.centerimage.js</p><pre class="brush:js;toolbar:false;">/**\r\n * description: position a image to center of container\r\n * author: Nicolas.Z\r\n * date: April 24, 2012\r\n * requires: jQuery v1.4+\r\n */\r\n(function( $ ) {\r\n       \r\nvar diminish = function( $target, maxWidth, maxHeight ) {\r\n    var width = $target.outerWidth(),\r\n        height = $target.outerHeight(),\r\n        verticalPadding = width - $target.width(), \r\n        horizontalPadding = height - $target.height();\r\n           \r\n    if ( width &gt; maxWidth ) {\r\n        height = height * ( maxWidth / width );\r\n        width = maxWidth;\r\n    }\r\n       \r\n    if ( height &gt; maxHeight ) {\r\n        width = width * ( maxHeight / height );\r\n        height = maxHeight;\r\n    }\r\n       \r\n    $target.css({\r\n        width: width - verticalPadding,\r\n        height: height - horizontalPadding\r\n    });\r\n       \r\n    return {\r\n        width: width,\r\n        height: height\r\n    };\r\n};\r\n   \r\n$.fn.center = function( options ) {\r\n    var settings = $.extend({\r\n        src: '''',\r\n        maxWidth: 0,\r\n        maxHeight: 0\r\n    }, options );\r\n       \r\n    return this.each(function() {\r\n        var $self = $( this ), \r\n            $container = $self.parent(),\r\n            containerWidth = $container.innerWidth(),\r\n            containerHeight = $container.innerHeight(),\r\n            rect, src;\r\n           \r\n        var center  = function() {\r\n            rect = diminish( $self, settings.maxWidth || containerWidth, settings.maxHeight || containerHeight );\r\n            $container.css( ''position'', ''relative'' );\r\n            $self.css({\r\n                ''position'': ''absolute'',\r\n                ''top'': ( containerHeight - rect.height ) / 2,\r\n                ''left'': ( containerWidth - rect.width ) / 2\r\n            });\r\n        };\r\n           \r\n        if ( $self.is( ''img'' ) ) {\r\n            $container.css( ''overflow'', ''hidden'' );\r\n            $self.bind( ''load'', function() {\r\n                // for IE\r\n                $self.unbind( ''load'' );\r\n                   \r\n                center();\r\n                $container.css( ''overflow'', ''visible'' );\r\n            });\r\n               \r\n            // for IE\r\n            if ( this.src ) {\r\n                src = this.src;\r\n            }\r\n            if ( settings.src &amp;&amp; typeof settings.src === ''string'' ) {\r\n                src = settings.src;\r\n            }\r\n            this.src = src;\r\n               \r\n        } else {\r\n            center();\r\n        }\r\n    });\r\n};\r\n   \r\n})( jQuery );</pre><p>Html</p><pre class="brush:html;toolbar:false;">jQuery - Plugin - Center Imagebody, div, p, ul, ol, li { padding: 0; margin: 0; }\r\n            body { font: 11px/1.6 Trebuchet MS; color: #333; }\r\n            ul { list-style: none; }\r\n            .clearfix { clear: both; }\r\n            #container { width: 760px; margin: 0 auto; }\r\n            #header, #content { margin-bottom: 15px; }\r\n            #footer { color: #666; }\r\n               \r\n            #text-box { width: 300px; height: 200px; margin-bottom: 15px; padding: 10px; border: solid 1px #ccc; background: #e8e8e8; }\r\n            #text-box-content { width: 300px; height: 200px; }\r\n            #text-box-content div { width: 100px; height: 100px; padding: 5px; box-shadow: 2px 2px 1px #aaa; border: solid 1px #aaa; background: #fff; }\r\n               \r\n            #photo-box { width: 450px; height: 300px; padding: 10px; border:solid 1px #ccc; background: #e8e8e8; }\r\n            #photo-box-content { width: 450px; height: 300px; }\r\n            #photo-box-content img { padding: 4px; border: solid 1px #bbb; box-shadow: 0 0 5px #999; background: #fff; }\r\n               \r\n            #image-index { margin: 15px 0; }\r\n            #image-index li { margin-right: 5px; float: left; }\r\n            #image-index a { padding: 10px; font-size: 18px; text-decoration: none; color: #666; border: solid 1px #ccc; float: left; }\r\n            #image-index a:hover { color: #000; text-shadow: 0 1px 0 #fff; background: #fc0; }jQuery - Plugin - Center Imagejquery plugin center imageImage 1Image 2Image 3Image 4Image 5© Copyright 2012 by Nicolas.Z</pre><p><br /></p><p><br /></p>', 1, '2012-06-26 02:52:25', 289, 0, '', '', ''),
(20, 2, 'clearfix清除浮动闭合容器之:after与:before', '首先对伪类:before与:after做下注脚:before 伪元素在元素之前添加内容。:after 伪元素在元素之后添加内容。这2个伪元素允许创作人员在元素内容的 最前面/最后面 插入生成内容。默认地，这个伪元素是行内元素，不过可以使用属性 display 改变这一点。该二伪类不被IE6/7所支持。好了，进入正题在平常我...', '<p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">首先对伪类:before与:after做下注脚</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">:before 伪元素在元素之前添加内容。</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">:after 伪元素在元素之后添加内容。</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">这2个伪元素允许创作人员在元素内容的 最前面/最后面 插入生成内容。默认地，这个伪元素是行内元素，不过可以使用属性 display 改变这一点。</span><span style="font-family:inherit;color:#000000"><strong style="font-weight:bold"><span style="font-family:inherit;color:#000000;font-size:12px">该二伪类不被IE6/7所支持</span></strong></span><span style="font-size:12px">。</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">好了，进入正题</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-family:inherit" id="more-214"></span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">在平常我们coding时候经常会发生子元素浮动而引起父级元素不能完全撑开的状况，</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">可能很多朋友给父级元素用overflow:hidden简单粗暴的解决，但这种方式有弊端，一是在IE6下不能完美解决，二是假如在该模块有定位的层就有定位层显示不完整的可能。</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">也有可能有些朋友是在父级元素里最后添加一个&lt;div style=”clear:both”&gt;&lt;/div&gt;来解决这个问题，但这样我们的HTML代码里就多了些冗余的代码且从语义化角度来看这也是不合理的。</span></p><p style="text-align:justify;line-height:24px;border-right-width:0px;background-color:#ffffff;font-family:&#39;helvetica neue&#39;, arial, helvetica, sans-serif;border-top-width:0px;border-bottom-width:0px;color:#222222;font-size:12px;vertical-align:baseline;border-left-width:0px;padding:0px;margin-top:0px;margin-bottom:10px;"><span style="font-size:12px">下面创建一个.clearfix类来完美解决这个问题</span></p><p><strong><span style="font-size:12px">代码如下：</span></strong></p><p><strong><span style="font-size:12px"></span></strong></p><pre class="brush:css;">.clearfix:after {content: ".";display: block;height: 0;clear: both;visibility: hidden;}  \r\n* html .clearfix{zoom:1;} /* IE6 */\r\n*+html .clearfix{zoom:1;} /* IE7 */</pre><p><strong><span style="font-size:12px">还可以:</span></strong><strong><span style="font-size:12px"></span></strong></p><pre class="brush:css;">clearfix:before, .clearfix:after { \r\n    content:""; \r\n    display:table; \r\n} \r\n.clearfix:after{ \r\n    clear:both; \r\n    overflow:hidden; \r\n} \r\n.clearfix{ \r\n    *zoom:1; \r\n}</pre><p> </p>', 1, '2012-08-02 03:47:41', 3251, 0, '', '', ''),
(3, 2, '快来看美女喽~', '', '<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<img data-original="http://img.baidu.com/hi/r/image/2012-02-10/5cd9b1b85518db186ad7d84257f4ac08.jpg" style="float:left;" border="0" height="284" hspace="0" vspace="0" width="186" />\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;2012年我省普通高考成绩将于6月26日下午公布。\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;考生可通过下列方式查询今年普通高考成绩：\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;一、96040电话查询方式，可提前拨打预定短信\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;中国电信固定电话、CDMA手机或小灵通，中国联通手机及铁通固定电话用户，只需拨通96040（全省各地均可直拨，无需加区号），按语音提示输入10位准考证号码和4位出生年月，即可查询到高考成绩。考生亦可提前拨打96040对高考成绩及录取情况进行短信通知预定，下发短信免费。\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;二、短信查询方式\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;（一）中国移动用户编写短信息“GAF+10位准考证号+4位出生年月”（例如准考证号是0123456789，出生年月是93年6月，则发送：GAF01234567899306，下同）；电信CDMA、小灵通及中国联通用户编写“KAF+10位准考证号+4位出生年月”，发送至10622168，即可预定或查询高考各科成绩及总分；\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;（二）广东地区的移动、联通用户可编写短信息&nbsp;“18+10位准考证号+4位出生年月”发送至10629500，即可预定或查询高考各科成绩及总分。\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;三、手机语音查询方式\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;广东地区的移动用户拨打1259025679，根据语音提示操作输入10位准考证号和4位出生年月，即可预定或查询高考各科成绩及总分。\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;四、网站在线查询方式\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;考生可登录广东考试服务网（网址：http://www.5184.com）进行查询。\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;通知单样式（理科类）\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;广东省2012年普通高考成绩通知单\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;考生号:××××××××××&nbsp;姓名:×××&nbsp;广东省教育考试院&nbsp;2012年6月26日\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;语文&nbsp;×××&nbsp;理科数学&nbsp;×××&nbsp;理科综合&nbsp;×××&nbsp;英语（含听说）&nbsp;×××\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;总成绩&nbsp;×××&nbsp;排位总分&nbsp;×××&nbsp;理科排位&nbsp;××××××\r\n</p>\r\n<p style="text-indent:2em">\r\n&nbsp;&nbsp;&nbsp;&nbsp;备注：总成绩指各科考试实际总成绩；排位总分指政策性加分后排位总分；理科排位指考生投档顺序位置\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n</p>', 1, '2012-06-26 05:34:37', 190, 0, '', '', ''),
(4, 2, 'iframe 高度自适应，结果2W多条。', '', '<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;<img data-original="/Public/Upload/96971340785841.jpg" style="width:314px;height:194px;float:right;" title="t010ba35b5b89b15f4a.jpg" border="0" hspace="0" vspace="0" />\r\n</p>\r\n<p>\r\n&nbsp;&nbsp;&nbsp;&nbsp;通过Google搜索iframe&nbsp;自适应高度，结果5W多条，搜索iframe&nbsp;高度自适应，结果2W多条。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;我翻了前面的几十条，刨去大量的转载，有那么三五篇是原创的。而这几篇原创里面，基本上只谈到如何自适应静的东西，就是没有考虑到JS操作DOM之后，如何做动态同步的问题。另外，在兼容性方面，也研究的不彻底。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;这篇文章，希望在这两个方面再做一些深入。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;可能有人还没接触到这个问题过，先说明一下，什么是自适应高度吧。所谓iframe自适应高度，就是，基于界面美观和交互的考虑，隐藏了iframe的border和scrollbar，让人看不出它是个iframe。如果iframe始终调用同一个固定高度的页面，我们直接写死iframe高度就可以了。而如果iframe要切换页面，或者被包含页面要做DOM动态操作，这时候，就需要程序去同步iframe高度和被包含页的实际高度了。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;顺便说下，iframe在迫不得已的时候才去用，它会给前端开发带来太多的麻烦。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;传统做法大致有两个：<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;方法一，在每个被包含页在本身内容加载完毕之后，执行JS取得本页面的高度，然后去同步父页面的iframe高度。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;方法二，在主页面iframe的onload事件中执行JS，去取得被包含页的高度内容，然后去同步高度。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;在代码维护角度考虑，方法二是优于方法一的，因为方法一，每个被包含页都要去引入一段相同的代码来做这个事情，创建了好多副本。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;两个方法都只处理了静的东西，就是只在内容加载的时候执行，如果JS去操作DOM引起的高度变化，都不太方便。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;如果在主窗口做一个Interval，不停的来获取被包含页的高度，然后做同步，是不是即方便，又解决了JS操作DOM的问题了呢？答案是肯定的。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n</p>', 1, '2012-06-27 08:31:59', 175, 0, '', '', ''),
(6, 3, 'jQuery点击空白地方关闭弹出层', '', '<p>jQuery点击空白地方关闭弹出层<br /></p><p><span style="color:#e36c09">全部代码 保存后可运行</span></p><pre class="brush:html;">&lt;!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"&gt;  \r\n&lt;html xmlns="http://www.w3.org/1999/xhtml"&gt;  \r\n&lt;head&gt;  \r\n&lt;title&gt;jQuery点击空白处关闭弹出层&lt;/title&gt;  \r\n&lt;meta http-equiv="content-type" content="text/html;charset=utf-8"&gt;  \r\n&lt;style type="text/css"&gt;  \r\n#box{width:300px;height:200px;border:1px solid #000;display:none; margin:0 auto;}  \r\n.btn{color:red;}  \r\n&lt;/style&gt;  \r\n&lt;script src="http://www.honoer.com/Public/Js/jQuery/jquery-1.6.2.min.js" type="text/javascript"&gt;&lt;/script&gt;  \r\n&lt;script type="text/javascript"&gt;  \r\n$(function(){  \r\n $(".btn").click(function(event){  \r\n  var e=window.event || event;  \r\n  if(e.stopPropagation){  \r\n   e.stopPropagation();  \r\n  }else{  \r\n   e.cancelBubble = true;  \r\n  }    \r\n  $("#box").show();  \r\n });  \r\n $("#box").click(function(event){  \r\n  var e=window.event || event;  \r\n  if(e.stopPropagation){  \r\n   e.stopPropagation();  \r\n  }else{  \r\n   e.cancelBubble = true;  \r\n  }  \r\n });  \r\n document.onclick = function(){  \r\n  $("#box").hide();  \r\n };  \r\n})  \r\n&lt;/script&gt;  \r\n&lt;/head&gt;  \r\n&lt;body&gt;  \r\n&lt;div id="box"&gt;打开我了,点空白关闭啊,谢谢&lt;/div&gt;  \r\n&lt;span class="btn"&gt;打开弹出层&lt;/span&gt;  \r\n&lt;/body&gt;  \r\n&lt;/html&gt;</pre><p><strong><span style="font-size:12px"> 又发现：</span></strong></p><p><strong><span style="font-size:12px"></span></strong> </p><pre class="brush:js;">&lt;script type="text/javascript"&gt;    \r\n    $(function(){     \r\n          \r\n        $("ul,input").click(function(e){     \r\n            e?e.stopPropagation():event.cancelBubble = true;     \r\n        });     \r\n                  \r\n        $("input").focus(function() {      \r\n        $("ul").fadeIn();      \r\n   